import React from 'react';
import axios from 'axios';

import CommnetList from './CommnetList';
import CommentForm from './CommentForm';

const CommentMain=(props)=>{
    const { id } = props.match.params;
    const [comments, setComments]= useState([]);

    const handleCommentSubmit = (event,comment)=>{
        event.preventDefault();
        axios.post(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`,comment)
        .then((response)=>{
            setComments([...comments, response.data]);
            //setComment(COMMENT_INITIAL_STATE);
        }).catch(error=>{console.log(error)})

    };
    return(
<React.Fragment>
       <CommnetList handleSubmit={handleCommentSubmit}/>
        <CommentForm handleSubmit={handleCommentSubmit}/>
</React.Fragment>

    );
} 

export default CommentMain;
