    // //     });
     },[])



    return (
        <React.Fragment>
            <h2> Comment Edit Form </h2>
            <div className="ui form">
                <div className="field">
                    <label>Name </label>
                    <input
                        name="display_name"
                        value={comment.display_name}
                        type="text"
                        onChange={onInputChange}

                    />
                </div>
                <div className="field">
                    <label>Comment Content</label>
                    <textarea
                        name="body"
                        value={comment.body}
                        rows="3"
                        onChange={onInputChange}

                    ></textarea>
                </div>
                <button className="ui primary button">
                    Send
                </button>
                <button className="ui button">
                    Cancel
                </button>
            </div>
        </React.Fragment>

    )
}

