import axios from 'axios';
import React, { useEffect, useState } from 'react';

const COMMENT_INITIAL_STATE = {
    display_name:'',
    body:''
};  

const PostDetail = (props) => {
    const [postDetail, setPostDetail]=useState({});
    const { id } = props.match.params;

    const [comments, setComments]= useState([]);
    // const [display_name, setDisplay_name]=useState('');
    // const [body, setBody]=useState('');
    const [comment, setComment]=useState(COMMENT_INITIAL_STATE)

    useEffect(()=>{
        axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}`)
        .then((response) => { setPostDetail(response.data) })
        .catch(error=>{console.log(error)});


        axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`)
        .then((response)=>{setComments(response.data)})
    },[])

    const handleCommentSubmit = (comment)=>{

        axios.post(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`,comment)
        .then((response)=>{
            setComments([...comments, response.data]);
            setComment(COMMENT_INITIAL_STATE);
        }).catch(error=>{console.log(error)})

    };


    const handleOnChange=(e)=>{
        setComment({...comment, [e.target.name]:e.target.value});
    };


    return (
    <React.Fragment>
        <h2 className="ui header">{postDetail.title}</h2>
        <p>{postDetail.created_at}</p>
        <p>{postDetail.content}</p>
        <h3>Comments</h3>
        {comments.map(comment=>{
            return(
                    <div key={comment.id} className="ui relaxed list">
                        <div className="item">
                            <img className="ui avatar image" src="/images/avatar/small/daniel.jpg"/>
                            <div className="content">
                            <a className="header">{comment.display_name}</a>
                            <div className="description">{comment.body}</div>
                            </div>
                        </div>
                    </div>
            )
        })}
        <h3>Write Comment</h3>
        <form className="ui form" onSubmit={(e)=>{
                e.preventDefault();
                handleCommentSubmit(comment);
        }}
           >
            <div className="ui small icon input">
                <input 
                    name='display_name'
                    type="text" 
                    placeholder="Write your name please!" 
                    onChange={handleOnChange } 
                    value={comment.display_name}/>
             </div>
             <textarea 
                    name='body'
                    placeholder="Tell us more" 
                    rows="3" 
                    onChange={handleOnChange} 
                    value={comment.body}>
            </textarea>
            <button className="ui blue button" type='submit'>Send</button>

        </form>


    </React.Fragment>)
}
export default PostDetail;
