import React,{useEffect,useState} from 'react';
//import { api } from '../api';
import { withRouter } from "react-router-dom";
import { api } from '../api';

const EditComment = (props) => {
//const {comments}=

    const [comment, setComment]=useState({display_name:"",body:""});
    //const { id } = props.match.params;

    //const comments=[props.comments];
    //console.log('editcommnet :',props);

    // const onInputChange = (e) => {
    //     setComment({ ...comment, [e.target.name]: e.target.value })
    // }

    useEffect(()=>{
        api()
        .get(`posts/${props.match.params.post_id}/comments`)
        .then(response=>{
            console.log('getresponsdata:',response.data);

            const comment = response.data.find(p=> p.id = props.match.params.id );



        })
    },[])

    // useEffect(()=>{

    //     api()
    //     .put(`/posts/${props.match.params.post_id}/comments/${props.match.params.id}`,)
    //     .then(response=>{
    // console.log(response.data);

    // // // //     });
    //  },[])



    return (
        <React.Fragment>
            <h2> Comment Edit Form </h2>
            <div className="ui form">
                <div className="field">
                    <label>Name </label>
                    <input
                        value={comment.display_name}
                        type="text"


                    />
                </div>
                <div className="field">
                    <label>Comment Content</label>
                    <textarea
                       value={comment.body}
                        rows="3"


                    ></textarea>
                </div>
                <button className="ui primary button">
                    Send
                </button>
                <button className="ui button">
                    Cancel
                </button>
            </div>
        </React.Fragment>

    )
}

export default withRouter(EditComment);
