import axios from 'axios';
import React, { useEffect, useState } from 'react';
import CommentMain from './CommnetMain';

const COMMENT_INITIAL_STATE = {
    display_name:'',
    body:''
};  

const PostDetail = (props) => {
    const [postDetail, setPostDetail]=useState({});
    const { id } = props.match.params;

    const [comments, setComments]= useState([]);
    // const [display_name, setDisplay_name]=useState('');
    // const [body, setBody]=useState('');
    const [comment, setComment]=useState(COMMENT_INITIAL_STATE)

    useEffect(()=>{ 
        axios.all(
            [
                axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}`),
                axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`)

            ]
        ).then(responses=>{
            setPostDetail(responses[0].data);
            setComments(responses[1].data)})
            .catch((error)=>{
                console.log(error);
            });

    },[])

    const handleCommentSubmit = (event)=>{
        event.preventDefault();
        axios.post(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`,comment)
        .then((response)=>{
            setComments([...comments, response.data]);
            setComment(COMMENT_INITIAL_STATE);
        }).catch(error=>{console.log(error)})

    };


    const handleOnChange=(e)=>{
        setComment({...comment, [e.target.name]:e.target.value});
    };


    return (
    <React.Fragment>
        <h2 className="ui header">{postDetail.title}</h2>
        <p>{postDetail.created_at}</p>
        <p>{postDetail.content}</p>

        <CommentMain comments={comments}/>

        <h3>Write Comment</h3>
        <form className="ui form" onSubmit={handleCommentSubmit}
           >
            <div className="ui small icon input">
                <input 
                    name='display_name'
                    type="text" 
                    placeholder="Write your name please!" 
                    onChange={handleOnChange } 
                    value={comment.display_name}/>
             </div>
             <textarea 
                    name='body'
                    placeholder="Tell us more" 
                    rows="3" 
                    onChange={handleOnChange} 
                    value={comment.body}>
            </textarea>
            <button className="ui blue button" type='submit'>Send</button>

        </form>


    </React.Fragment>)
}
export default PostDetail;
