import React, { useState, useEffect } from 'react';
import { api } from '../api';
import { withRouter } from "react-router-dom";

const EditComment = (props) => {

   console.log('editcommnet',props);
    const [comment, setComment]=useState({display_name:"props.comments.display_name",body:"props.comments.body"});
    //const { id } = props.match.params;


    const onInputChange = (e) => {
        setComment({ ...comment, [e.target.name]: e.target.value })
    }



    // useEffect(()=>{
    //     api()
    //     .put(`/posts/${props.match.params.post_id}/comments/${props.match.params.id}`)
    //     .then(response=>{
    //         console.log(response.data);

    //     });
    // },[])



    return (
        <React.Fragment>
            <h2> Comment Edit Form </h2>
            <div className="ui form">
                <div className="field">
                    <label>Name </label>
                    <input
                        name="display_name"
                        value={comment.display_name}
                        type="text"
                        onChange={onInputChange}

                    />
                </div>
                <div className="field">
                    <label>Comment Content</label>
                    <textarea
                        name="body"
                        value={comment.body}
                        rows="3"
                        onChange={onInputChange}

                    ></textarea>
                </div>
                <button className="ui primary button">
                    Send
                </button>
                <button className="ui button">
                    Cancel
                </button>
            </div>
        </React.Fragment>

    )
}

export default withRouter(EditComment);
