import axios from 'axios';
import React, { useEffect, useState } from 'react';

const PostDetail = (props) => {
    const [postDetail, setPostDetail]=useState({});
    const { id } = props.match.params;

    const [comments, setComments]= useState([]);
    const [display_name, setDisplay_name]=useState('');
    const [body, setBody]=useState('');


    useEffect(()=>{
        axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}`)
        .then((response) => { setPostDetail(response.data) })
        .catch(error=>{console.log(error)});


        axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`)
        .then((response)=>{setComments(response.data)})


    },[])

    const handleCommentSubmit = ()=>{
        axios.post(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`,{display_name,body})
        .then(response=>{console.log(response)})
    }


    return (
    <React.Fragment>
        <h2 className="ui header">{postDetail.title}</h2>
        <p>{postDetail.created_at}</p>
        <p>{postDetail.content}</p>
        <h3>Comments</h3>
        {comments.map(comment=>{
            return(
                    <div key={comment.id} className="ui relaxed list">
                        <div className="item">
                            <img className="ui avatar image" src="/images/avatar/small/daniel.jpg"/>
                            <div className="content">
                            <a className="header">{comment.display_name}</a>
                            <div className="description">{comment.body}</div>
                            </div>
                        </div>
                    </div>
            )
        })}
        <h3>Write Comment</h3>
        <form className="ui form">
            <div className="ui small icon input">
                <input type="text" placeholder="Write your name please!" onChange={(e)=>{
                    setDisplay_name(e.target.value)}} value={display_name}/>
             </div>
             <textarea placeholder="Tell us more" rows="3" onChange={(e)=>{
                    setBody(e.target.value)}} value={body}>
            </textarea>
        </form>


    </React.Fragment>)
}
export default PostDetail;
